import React, { Component } from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, NavSearch, SearchInfo, SearchInfoTitle, SearchInfoSwitch, SearchInfoList, SearchInfoItem, SearchWrapper, Addition, Button } from './style.js';
import { CSSTransition } from 'react-transition-group';
import { actionCreators } from './store';
import { connect } from 'react-redux';

class Header extends Component {

    searchInfo(shown, list, page, totalPage) {
        list = list.slice((page-1)*10,page*10);
        if (shown) {
            return (
            <SearchInfo>
                <SearchInfoTitle>
                    热门搜索
                    <SearchInfoSwitch>换一批</SearchInfoSwitch>
                </SearchInfoTitle>
                <SearchInfoList>
                    {list.map((item, index) => {
                        return <SearchInfoItem key={index}>{item}</SearchInfoItem>
                    })}
                </SearchInfoList>
            </SearchInfo> )
        }
    }

    render() {
        const { focused, list, handleFocus, handleUnFocus, page, totalPage } = this.props;
        return (
            <HeaderWrapper>
                <Logo />
                <Nav>
                    <NavItem className="left shouyeInNavItem">首页</NavItem>
                    <NavItem className="left">下载App</NavItem>
                    <NavItem className="right">登录</NavItem>
                    <NavItem className="right diamond"></NavItem>
                    <NavItem className="right">
                        <span className="iconfont Aa">&#xe636;</span>
                    </NavItem>
                    <SearchWrapper>
                        <CSSTransition in={focused} timeout={350} classNames="slide">
                            <NavSearch className={focused ? 'focused' : ''} onFocus={handleFocus} onBlur={handleUnFocus}></NavSearch>
                        </CSSTransition>
                            <span className={focused ? 'focused iconfont' : 'iconfont'}>&#xe62d;</span>
                        {this.searchInfo(focused,list,page,totalPage)}
                    </SearchWrapper>
                </Nav>
                <Addition>
                    <Button className="writting">
                    <span className="iconfont">&#xe66f;</span> 写文章
                    </Button>
                    <Button className="reg">注册</Button>
                </Addition>
            </HeaderWrapper>
        )
    }
}

const mapStateToProps = (state) => {

    return {
        focused: state.get('header').get('focused'),
        list: state.get('header').get('list'),
        page: state.get('header').get('page'),
        totalPage: state.get('header').get('totalPage')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleFocus() {
            // 获取AJAX
            dispatch(actionCreators.getList())
            dispatch(actionCreators.focus_on);
        },
        handleUnFocus() {
            dispatch(actionCreators.focus_off);
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Header);
