import React, { Component } from 'react';
import List from './components/List';
import Recommend from './components/Recommend';
import Writer from './components/Writer';
import { HomeWrapper, HomeLeft, HomeRight, BackTop } from './style';
import { connect } from 'react-redux';
import * as actionCreators from './store/actionCreators';

class Home extends Component {
    render() {
        return (
            <HomeWrapper>
                <HomeLeft>
                    <List />
                </HomeLeft>
                <HomeRight>
                    <Recommend />
                    <Writer />
                </HomeRight>
                {this.props.backToTopShown ? <BackTop onClick={this.backToTop}>ä¸ª</BackTop> : ''}
            </HomeWrapper>
        );
    }

    componentDidMount() {
        this.props.generateList();
        this.bindEvents();
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.props.changeScrollShow)
    }

    bindEvents(){
        window.addEventListener('scroll', this.props.changeScrollShow)
    }

    backToTop(){
        window.scrollTo(0,0);
    }
}

const mapStateToProps = (state) => {
    return {
        backToTopShown: state.get('home').get('backToTopShown')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        generateList() {
            dispatch(actionCreators.generateList());
        },
        changeScrollShow() {
            if(document.documentElement.scrollTop > 300) {
                dispatch(actionCreators.scrollTopShow);
            } else {
                dispatch(actionCreators.scrollTopUnshow);
            }
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Home);
