import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Card, Icon, List, Modal, DatePicker, Input } from 'antd';

const { TextArea } = Input;

class todoList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            deleteConfirm: false,
            moveConfirm: false,
            detailConfirm: false,
            item: undefined,
            newItem: {
                duedate: 'Selete Date',
                title: '',
                briefDescription: ''
            },
            tempItem: {}
        };
    }

    render() {
        const todoList = this.props.todoList;
        const item = this.state.item ? this.state.item : this.state.newItem
        return ( 
            <div>
                <Card title={this.props.message} extra={<Icon type="plus" onClick={this.handleAddTask.bind(this)}/>} style={{ margin: '80px 0 0 10%', width: '80%' }}>
                <List
                itemLayout="horizontal"
                dataSource={todoList}
                renderItem={item => (
                    <List.Item>
                        <List.Item.Meta
                        title={item.briefDescription}
                        description={item.duedate}
                        />
                        <Button shape="circle" icon="edit" style={{ marginRight: '20px' }} onClick={this.showDetailModal.bind(this,item)} />
                        <Button shape="circle" icon={ this.props.message === "Todo List" ? "check" : "redo"} onClick={this.showMoveModal.bind(this,item)} style={{ marginRight: '20px' }} />
                        <Button shape="circle" icon="delete" style={{ marginRight: '20px' }} onClick={this.showDeleteModal.bind(this,item)}/>
                    </List.Item>
                )}
                />
                </Card>

                <Modal
                    title="Warning!"
                    visible={this.state.deleteConfirm}
                    onOk={this.handleDeleteOk.bind(this)}
                    onCancel={this.handleDeleteCancel.bind(this)}
                    okText="Yes"
                    cancelText="No"
                    okButtonProps={{ type:"danger" }}
                    cancelButtonProps={{ type:"primary" }}
                    >
                    Are you sure you want to delete this task?
                </Modal>

                <Modal
                    title={this.props.message === "Todo List" ? "Finish The Task" : "Redo The Task"} 
                    visible={this.state.moveConfirm}
                    onOk={this.handleMoveOk.bind(this)}
                    onCancel={this.handleMoveCancel.bind(this)}
                    okText="Yes"
                    cancelText="No"
                    >
                    Do you want to move this task to {this.props.message === "Todo List" ? "Done List?" : "Todo List?"}
                </Modal>

                <Modal
                    style={{ width: '100%' }}
                    title={'Description'} 
                    visible={this.state.detailConfirm}
                    onOk={this.handleDetailOk.bind(this)}
                    onCancel={this.handleDetailCancel.bind(this)}
                    okText="Submit"
                    cancelText="Discard"
                    >
                    <h3 style={{ float: 'left', marginRight: '5%'}}>Brief description: </h3>
                    <TextArea value={item.briefDescription} style={{ width: '60%' }} onChange={this.briefDescriptionOnChange.bind(this)} autoSize={{ maxRows: 3 }} />
                    <div></div>
                    <br />
                    <h3 style={{ float: 'left', marginRight: '17%'}}>Due Date: </h3>
                    <DatePicker onChange={this.dueDateOnChange.bind(this)} placeholder={item.duedate}/>
                </Modal>

            </div>
        )
    }

    // Delete Task

    handleDelete(){
        this.props.handleDeleteTask(this.props.message,this.props.todoList.indexOf(this.state.item))
    }

    showDeleteModal(item) {
        this.setState({
            deleteConfirm: true,
            item: item
        });
    };

    handleDeleteOk() {
        this.setState({
            deleteConfirm: false,
        });
        this.handleDelete();
    };

    handleDeleteCancel() {
        this.setState({
            deleteConfirm: false,
        });
    };

    // Move Task

    handleMoveTask(){
        this.props.handleMoveTask(this.props.message,this.props.todoList.indexOf(this.state.item),this.state.item)
    }

    showMoveModal(item) {
        this.setState({
            moveConfirm: true,
            item: item
        });
    };

    handleMoveOk() {
        this.setState({
            moveConfirm: false,
        });
        this.handleMoveTask();
    };

    handleMoveCancel() {
        this.setState({
            moveConfirm: false,
        });
    };

    // Edit Task & New Task

    dueDateOnChange(date, dateString) {
        var copyItem = this.state.item;
        copyItem.duedate = dateString;
        this.setState({
            item: copyItem
        });
    }

    briefDescriptionOnChange({ target: { value } }){
        var copyItem = this.state.item;
        copyItem.briefDescription = value;
        this.setState({
            item: copyItem
        });
    }

    handleDetailTask(){
        this.props.handleModifyTask(this.props.message,this.props.todoList.indexOf(this.state.item),this.state.item)
    }

    handleAddTask(){
        const newItem = JSON.parse(JSON.stringify(this.state.newItem));
        this.setState({
            detailConfirm: true,
            item: newItem
        })
    }

    showDetailModal(item) {
        this.setState({
            detailConfirm: true,
            item: item,
            tempItem: item
        });
    };

    handleDetailOk() {
        this.setState({
            detailConfirm: false
        });
        this.handleDetailTask();
    };

    handleDetailCancel() {
        const originalItem = JSON.parse(JSON.stringify(this.state.tempItem))
        console.log(originalItem)
        this.setState({
            detailConfirm: false,
            item: originalItem
        });
    };
}

const mapStateToProps = (state) => {
    return {

    }
}

const mapDispatchToProps = (dispatch) => {
    return {

    }
}

export default connect(mapStateToProps,mapDispatchToProps)(todoList);
