import styled from 'styled-components';
import list_pic_1 from '../../statics/list-pic-1.jpeg'

export const HomeWrapper = styled.div`
    width: 960px;
    margin: 0 auto;
    overflow: hidden;
`;

export const HomeLeft = styled.div`
    margin-left: 15px;
    padding-top: 30px;
    width: 625px;
    float: left;
`;

export const HomeRight = styled.div`
    width: 240px;
    float: right;
`;

export const ListItem = styled.div`
    padding: 20px 0;
    border-bottom: 1px solid #dcdcdc;
    overflow: hidden;
    .list-pic {
        display: block;
        float: right;
        width: 125px;
        height: 100px;
        border-radius: 10px;
        background: url(${list_pic_1});
        background-size: 100%;
    }
`;

export const ListInfo = styled.div`
    width: 500px;
    float: left;
    .title {
        line-height: 27px;
        font-size: 18px;
        font-weight: bold;
        color: #333;
    }
    .desc {
        line-height: 24px;
        font-size: 13px;
        color: #999;
    }
`;
