import React, { Component } from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, NavSearch, SearchWrapper, Addition, Button } from './style.js';
import { CSSTransition } from 'react-transition-group';

export default class Header extends Component {

    constructor(props){
        super(props);
        this.state = {
            focused: false
        }
        this.handleFocus = this.handleFocus.bind(this)
    }

    render() {
        return (
            <HeaderWrapper>
                <Logo />
                <Nav>
                    <NavItem className="left shouyeInNavItem">首页</NavItem>
                    <NavItem className="left">下载App</NavItem>
                    <NavItem className="right">登录</NavItem>
                    <NavItem className="right diamond"></NavItem>
                    <NavItem className="right">
                        <span className="iconfont Aa">&#xe636;</span>
                    </NavItem>
                    <SearchWrapper>
                        <NavSearch className={this.state.focused ? 'focused' : ''} onFocus={this.handleFocus} onBlur={this.handleFocus}></NavSearch>
                        <span className={this.state.focused ? 'focused iconfont' : 'iconfont'}>&#xe62d;</span>
                    </SearchWrapper>
                </Nav>
                <Addition>
                    <Button className="writting">
                    <span className="iconfont">&#xe66f;</span> 写文章
                    </Button>
                    <Button className="reg">注册</Button>
                </Addition>
            </HeaderWrapper>
        )
    }

    handleFocus(){
        this.setState({
            focused: !this.state.focused
        })
    }
}
