import * as actionTypes from './actionTypes'
import axios from 'axios'

// -------------------------- private  -------------------------

const generate_initial_list = (value) => ({
    type: actionTypes.GENERATE_INITIAL_LIST,
    value : value,
    totalPage: Math.ceil(value.length / 10)
});

// -------------------------- public ---------------------------

export const focus_on =  {
    type: actionTypes.FOCUS_ON
};

export const focus_off =  {
    type: actionTypes.FOCUS_OFF
};

export const mouse_enter = {
    type: actionTypes.MOUSE_ENTER
};

export const mouse_out = {
    type: actionTypes.MOUSE_OUT
};

export const getList = () => {
    return (dispatch) => {
        axios.get('/api/headerList.json').then((res) => {
            dispatch(generate_initial_list(res.data.data))
        }).catch(() => {
            console.log('error')
        })
    }
}
