import * as actionTypes from './actionTypes';
import { fromJS } from 'immutable';

const defaultState = fromJS({
    focused: false,
    mouseIn: false,
    page: 1,
    totalPage: -1
});

export default (state = defaultState, action) => {
    switch (action.type) {
        case (actionTypes.FOCUS_ON): 
            // 此处，state并非被修改，而是系统创建了一个新的state，
            // 因此必须使用return
            return state.set('focused',true);
        case (actionTypes.FOCUS_OFF): 
            return state.set('focused',false);
        case(actionTypes.MOUSE_ENTER):
            return state.set('mouseIn',true);
        case(actionTypes.MOUSE_OUT):
            return state.set('mouseIn',false);
        case(actionTypes.HUANYIPI):
            return state.set('page',action.value);
        case (actionTypes.GENERATE_INITIAL_LIST): 
            return state.merge({
                list: fromJS(action.value),
                totalPage: action.totalPage
            })
        default: return state;
    }
}
