import React, { Component } from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, NavSearch, SearchInfo, SearchInfoTitle, SearchInfoSwitch, SearchInfoList, SearchInfoItem, SearchWrapper, Addition, Button } from './style.js';
import { CSSTransition } from 'react-transition-group';
import { actionCreators } from './store';
import { connect } from 'react-redux';

class Header extends Component {
    render() {
        return (
            <HeaderWrapper>
                <Logo />
                <Nav>
                    <NavItem className="left shouyeInNavItem">首页</NavItem>
                    <NavItem className="left">下载App</NavItem>
                    <NavItem className="right">登录</NavItem>
                    <NavItem className="right diamond"></NavItem>
                    <NavItem className="right">
                        <span className="iconfont Aa">&#xe636;</span>
                    </NavItem>
                    <SearchWrapper>
                        <CSSTransition in={props.focused} timeout={350} classNames="slide">
                            <NavSearch className={props.focused ? 'focused' : ''} onFocus={props.handleFocus} onBlur={props.handleUnFocus}></NavSearch>
                        </CSSTransition>
                            <span className={props.focused ? 'focused iconfont' : 'iconfont'}>&#xe62d;</span>
                        {searchInfo(props.focused)}
                    </SearchWrapper>
                </Nav>
                <Addition>
                    <Button className="writting">
                    <span className="iconfont">&#xe66f;</span> 写文章
                    </Button>
                    <Button className="reg">注册</Button>
                </Addition>
            </HeaderWrapper>
        )
    }
}

const searchInfo = (shown) => {
    if (shown) {
        return (
        <SearchInfo>
            <SearchInfoTitle>
                热门搜索
                <SearchInfoSwitch>换一批</SearchInfoSwitch>
            </SearchInfoTitle>
            <SearchInfoList>
                <SearchInfoItem>教育</SearchInfoItem>
                <SearchInfoItem>简书</SearchInfoItem>
                <SearchInfoItem>生活</SearchInfoItem>
                <SearchInfoItem>投稿</SearchInfoItem>
                <SearchInfoItem>历史</SearchInfoItem>
                <SearchInfoItem>PHP</SearchInfoItem>
                <SearchInfoItem>考研</SearchInfoItem>
                <SearchInfoItem>docker</SearchInfoItem>
                <SearchInfoItem>ECHO</SearchInfoItem>
                <SearchInfoItem>微信小程序</SearchInfoItem>
            </SearchInfoList>
        </SearchInfo> )
    }
}

const mapStateToProps = (state) => {
    return {
        focused: state.get('header').get('focused')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleFocus() {
            dispatch(actionCreators.focus_on);
        },
        handleUnFocus() {
            dispatch(actionCreators.focus_off);
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Header);
