import React, { PureComponent } from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, ThemeSelector, NavSearch, SearchWrapper, Addition, Button } from './style.js';
import { CSSTransition } from 'react-transition-group';
import { actionCreators } from './store';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

class Header extends PureComponent {

    constructor(props){
        super(props);
        this.state = {
            themeFocused: false
        };
        this.handleThemeFocus = this.handleThemeFocus.bind(this);
        this.handleThemeUnFocus = this.handleThemeUnFocus.bind(this);

    }

    render() {
        const { focused, handleFocus, handleUnFocus, themeList, themeSelected } = this.props;
        return (
            <HeaderWrapper>
                <Link to='/'><Logo /></Link>
                <Nav>
                <Link to='/'><NavItem className="left backToMainPage">Main page</NavItem></Link>
                    <NavItem className="right" onClick={this.changeTheme.bind(this,themeList,themeSelected)}>Theme</NavItem>
                    <CSSTransition in={this.state.themeFocused} timeout={500} classNames="themeSlide">
                        <ThemeSelector className={this.state.themeFocused ? 'themeFocused' : 'themeUnfocused'} onFocus={this.handleThemeFocus} onBlur={this.handleThemeUnFocus}></ThemeSelector>
                    </CSSTransition>
                    <SearchWrapper>
                        <CSSTransition in={focused} timeout={350} classNames="slide">
                            <NavSearch className={focused ? 'focused' : ''} onFocus={handleFocus} onBlur={handleUnFocus}></NavSearch>
                        </CSSTransition>
                            <span className={focused ? 'focused iconfont zoom' : 'iconfont zoom'}>&#xe62d;</span>
                    </SearchWrapper>
                </Nav>
                <Addition>
                    <Button className="reg">My account</Button>
                </Addition>
            </HeaderWrapper>
        )
    }

    componentDidMount() {
        this.props.setTheme();
    }

    changeTheme(themeList,themeSelected) {
        document.body.style.backgroundImage = themeList.getIn([themeSelected+1,'pic']);
        // give store the new themeSelected
    }

    handleThemeFocus() {
        this.setState({
            themeFocused: true
        })
    }

    handleThemeUnFocus() {
        this.setState({
            themeFocused: false
        })
    }
}

const mapStateToProps = (state) => {
    return {
        focused: state.get('header').get('focused'),
        themeList: state.get('header').get('themeList'),
        themeSelected: state.get('header').get('themeSelected')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleFocus() {
            dispatch(actionCreators.focus_on);
        },
        handleUnFocus() {
            dispatch(actionCreators.focus_off);
        },
        setTheme() {
            dispatch(actionCreators.generateTheme());
            document.body.style.backgroundImage = 'url("https://images.wallpaperscraft.com/image/beautiful_scenery_mountains_lake_nature_93318_1920x1080.jpg")';
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Header);
