import React, { Component } from 'react';
import { ListItem, ListInfo, LoadMore } from '../style'
import { connect } from 'react-redux';
import * as actionCreators from '../store/actionCreators'

class List extends Component {

    listItem(item) {
        return (
            <ListItem key={item.get('id')} imgurl={item.get('pic')}>
                <ListInfo>
                    <h3 className='title'>{item.get('title')}</h3>
                    <p className='desc'>{item.get('desc')}</p>
                </ListInfo>
                <div className="list-pic"/>
            </ListItem>
        )
    }

    render() {
        console.log(this.props.pageNow)
        const newArticleList = this.props.articleList.slice(0, this.props.pageNow * 10);
        return (
            <div>
                {newArticleList.map((item) => {
                    return this.listItem(item);
                })}
                <LoadMore onClick={this.props.getMoreList}> {this.props.lastArticle ? '已经是最后一篇了' : '更多文章'}</LoadMore>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        articleList: state.get('home').get('articleList'),
        pageNow: state.get('home').get('pageNow'),
        lastArticle: state.get('home').get('lastArticle')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        getMoreList() {
            dispatch(actionCreators.morePage)
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(List);
