import { actionTypes } from '../store';

const defaultState = {
    focused: false
}

export default (state = defaultState, action) => {
    const newState = JSON.parse(JSON.stringify(state))
    if (action.type === actionTypes.CHANGE_FOCUS) {
        newState.focused = !newState.focused;
    }
    return newState;
}
