import React, { PureComponent } from 'react';
import { HeaderWrapper, Logo, Nav, NavItem, NavSearch, SearchWrapper, Addition, Button } from './style.js';
import { CSSTransition } from 'react-transition-group';
import { actionCreators } from './store';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

class Header extends PureComponent {

    render() {
        const { focused, handleFocus, handleUnFocus } = this.props;
        return (
            <HeaderWrapper>
                <Link to='/'><Logo /></Link>
                <Nav>
                    <NavItem className="left shouyeInNavItem">首页</NavItem>
                    <NavItem className="left">下载App</NavItem>
                    <NavItem className="right">登录</NavItem>
                    <NavItem className="right diamond"></NavItem>
                    <NavItem className="right">
                        <span className="iconfont Aa">&#xe636;</span>
                    </NavItem>
                    <SearchWrapper>
                        <CSSTransition in={focused} timeout={350} classNames="slide">
                            <NavSearch className={focused ? 'focused' : ''} onFocus={handleFocus} onBlur={handleUnFocus}></NavSearch>
                        </CSSTransition>
                            <span className={focused ? 'focused iconfont zoom' : 'iconfont zoom'}>&#xe62d;</span>
                    </SearchWrapper>
                </Nav>
                <Addition>
                    <Button className="writting">
                    <span className="iconfont">&#xe66f;</span> 写文章
                    </Button>
                    <Button className="reg">注册</Button>
                </Addition>
            </HeaderWrapper>
        )
    }
}

// icon rotation
var rotation = 0;

const mapStateToProps = (state) => {
    return {
        focused: state.get('header').get('focused'),
        mouseIn: state.get('header').get('mouseIn'),
        page: state.get('header').get('page'),
        totalPage: state.get('header').get('totalPage')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        handleList() {
            dispatch(actionCreators.getList());
        },
        handleFocus() {
            dispatch(actionCreators.focus_on);
        },
        handleUnFocus() {
            dispatch(actionCreators.focus_off);
        },
        handleMouseEnter() {
            dispatch(actionCreators.mouse_enter);
        },
        handleMouseOut() {
            dispatch(actionCreators.mouse_out);
        },
        huanYiPi(page, totalPage, spin) {
            // icon rotation
            spin.style.transform = 'rotate(' + (rotation+=360) + 'deg)';
            // change page
            var newPage = page + 1;
            if (page === totalPage) newPage = 1;
            dispatch(actionCreators.huanYiPi(newPage));
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Header);
