import { fromJS, List } from 'immutable';
import * as actionTypes from './actionTypes';

const defaultState = fromJS({
    initial: true,
    list: [],
    userList: []
});

export default (state = defaultState, action) => {
    switch (action.type) {
        case (actionTypes.GET_DETAIL):
            return state.set('list', fromJS(action.list))
        case (actionTypes.GET_INITIAL_TODOLIST):
            return state.set('userList', fromJS(action.userList)).set('initial', false)
        case (actionTypes.DELETE_TASK):
            // var abc = state.get('userList').getIn([action.whichUser,action.whichList]);
            const abc = List([4,3,2,1,0])
            console.log('old ' + abc.toJS());
            const cba = abc.delete(0);
            console.log('new ' + cba.toJS());
            // console.log(state.get('userList').getIn([action.whichUser]).set(action.whichList, fromJS(abc)).toJS())
            // console.log(state.get('userList').getIn([action.whichUser,action.whichList]))
            return state
        default: return state;
    }
}
