import React, { PureComponent } from 'react';
import List from './components/List';
import Recommend from './components/Recommend';
import Dashboard from './components/Dashboard';
import { HomeWrapper, HomeLeft, HomeRight, BackTop } from './style';
import { connect } from 'react-redux';
import * as actionCreators from './store/actionCreators';
import { base } from '../../service/firebase.conf';

class Home extends PureComponent {

    constructor(props){
        super(props);
        this.state = {
            backToTopShown: false
        };
        this.changeScrollShow = this.changeScrollShow.bind(this);
    }

    render() {
        return (
            <HomeWrapper>
                <HomeLeft>
                    <List />
                </HomeLeft>
                <HomeRight>
                    <Recommend />
                    <Dashboard />
                </HomeRight>
                {this.state.backToTopShown ? <BackTop onClick={this.backToTop}>ä¸ª</BackTop> : ''}
            </HomeWrapper>
        );
    }

    componentWillMount() {
        this.props.bindDatabaseWithLocal();
    }

    componentDidMount() {
        this.props.generateList();
        this.bindEvents();
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.changeScrollShow)
        base.removeBinding(this.hahaRef);
    }

    bindEvents(){
        window.addEventListener('scroll', this.changeScrollShow)
    }

    backToTop(){
        window.scrollTo(0,0);
    }

    changeScrollShow() {
        if(document.documentElement.scrollTop > 300) {
            this.setState({
                backToTopShown : true
            });
        } else {
            this.setState({
                backToTopShown : false
            });
        }
    }
}

const mapStateToProps = (state) => {
    return {
        haha: state.get('home').get('haha')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        generateList() {
            dispatch(actionCreators.generateList());
        },
        bindDatabaseWithLocal() {
            dispatch(actionCreators.bindDatabaseWithLocal);
        }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Home);
