import { fromJS } from 'immutable';

const defaultState = fromJS({
    articleList: [{
        id: 1,
        title: '这套瑜伽练习每天练，自然瘦腰瘦腿不反弹！',
        desc: '俗话说：“三月四月不减肥，五月六月徒伤悲”。天气渐渐回暖，又快到了露胳膊露腿的季节了，新一轮的减肥计划又该提上日程了！ 今天给大家推荐一套瑜伽练...'
    }]
});

export default (state = defaultState, action) => {
    switch(action.type) {
        default:
            return state;
    }
}
