import React, { PureComponent } from 'react';
import * as actionCreators from './store/actionCreators'
import { connect } from 'react-redux';
import { Card, Icon, List } from 'antd';

class todoList extends PureComponent {

    render() {
        // const a = this.props.userList.getIn([this.props.id-1,'todoList']).getIn([0,'briefDescription'])
        const todoList = this.props.userList;
        var count = -1;
        // var abc = JSON.stringify(todoList);
        // const abc = JSON.parse(JSON.stringify(todoList));
        // console.log(abc);
        // console.log(JSON.parse(abc))
        return ( 
            <Card title="Todo List" extra={<Icon type="plus" />} style={{ margin: '80px 0 0 10%', width: '80%' }}>
            <List
            itemLayout="horizontal"
            dataSource={todoList}
            renderItem={item => (
            <List.Item>
                <List.Item.Meta
                title={todoList.getIn([++count,'briefDescription'])}
                description={todoList.getIn([++count,'duedate'])}
                />
            </List.Item>
            )}
            />
            </Card>
        )
    }

    // componentDidMount() {
    //     if (this.props.initial) {
    //         this.props.getInitialTodoList();
    //     }
    // }
}

const mapStateToProps = (state) => {
    return {
        // initial: state.get('detail').get('initial'),
        // userList: state.get('detail').get('userList')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
    //    getInitialTodoList() {
    //        dispatch(actionCreators.getTodoList());
    //    }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(todoList);
