import React, { PureComponent } from 'react';
import { DetailWrapper, Header, Content } from './style';
import * as actionCreators from './store/actionCreators'
import { connect } from 'react-redux';

class Detail extends PureComponent {
    render() {
        const newList = this.props.list;
        const id = this.props.match.params.id;
        return ( 
            <DetailWrapper>
                <Header>{newList.getIn([id-1,'title'])}</Header>
                {/* <Content dangerouslySetInnerHTML={{__html: newList.getIn([id-1,'content'])}} /> */}
                <Content>
                    <div class='pic-div'><img src='https://images.unsplash.com/photo-1489595812352-d3db436c102b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=400&q=60' alt=''/></div><p>200年后，人类从废墟中发现一个满尘埃的硬盘，解码其中的数据，会发现在他们祖宗的世界，不但美可以受人追捧，丑也行。</p><p>乔碧萝殿下在遭斗鱼封禁后，偷偷去了B站开直播。</p><p>结果开播仅一个小时收礼主播等级就达到37级，人气超百万。</p>
                </Content>
            </DetailWrapper>
        )
    }

    componentDidMount() {
        this.props.getData(this.props.match.params.id);
    }
}

const mapStateToProps = (state) => {
    return {
        list: state.get('detail').get('list')
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
       getData(id) {
           dispatch(actionCreators.getDetail(id));
       }
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Detail);
